@app.cell
def _(Dataset, data):
    dataset = Dataset.from_list(data)
    label2id = {"interviewer": 0, "interviewee": 1}
    dataset = dataset.map(lambda x: {"label": label2id[x["label"]]})
    return (dataset,)


